import chroma from 'chroma-js';

export const calculatePalette = (baseHex) => {
  // chroma-js를 이용해 명도(lightness) 기준으로 100~900 단계를 만듭니다.
  const base = chroma(baseHex);
  const palette = [];
  let targetLevel = 500; // 기본값

  // HLS 변환 후 로직 (기존 파이썬 로직과 유사하게 구현)
  const l = base.get('hsl.l');
  targetLevel = Math.max(100, Math.min(900, Math.round(l * 10) * 100));

  // 100(밝음) ~ 900(어두움) 생성
  // chroma.scale을 사용해 흰색 -> 본색 -> 검은색 그라데이션을 만듭니다.
  const scale = chroma.scale(['#fff', baseHex, '#000']).domain([0, 0.5, 1]).mode('lch');

  [100, 200, 300, 400, 500, 600, 700, 800, 900].forEach((level) => {
    // level이 높을수록(900) 어둡게(1에 가깝게) 매핑
    const t = level / 1000; 
    const color = scale(t).hex().toUpperCase();

    palette.push({
      level: String(level),
      hex: color,
      isTarget: level === targetLevel 
    });
  });

  return { palette, targetLevel };
};
