import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Plus, Save, User, Send, Folder, MoreHorizontal, Edit3, Star, StarOff, Copy } from 'lucide-react'; 
import './App.css'; 

const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('designBotUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [projects, setProjects] = useState({ "기본 프로젝트": [] });
  const [activeProject, setActiveProject] = useState("기본 프로젝트");
  const [inputHex, setInputHex] = useState("");
  const [currentData, setCurrentData] = useState(null);
  const [loading, setLoading] = useState(false);
  const scrollRef = useRef(null);

  // --- [추가됨] 토스트 메시지 상태 ---
  const [toast, setToast] = useState(null);

  // --- [메뉴 & 이름 변경 상태] ---
  const [menuOpenId, setMenuOpenId] = useState(null);
  const [editingProject, setEditingProject] = useState(null);
  const [tempName, setTempName] = useState("");

  useEffect(() => {
    if (user && user.email) {
      fetchUserData(user.email);
    }
  }, [user]);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    localStorage.setItem('designBotUser', JSON.stringify(decoded)); 
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5001/api/projects/${email}`);
      setProjects(res.data || { "기본 프로젝트": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "기본 프로젝트": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setCurrentData(null);
    setProjects({ "기본 프로젝트": [] });
    localStorage.removeItem('designBotUser'); 
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("올바른 HEX 코드를 입력해주세요 (예: #3A86FF)");
        return;
    }

    setLoading(true);
    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);

    try {
      const res = await axios.post('http://localhost:5001/api/ai-naming', { hex: formattedHex });
      setCurrentData({ userInput: formattedHex, name: res.data.name, palette, target: targetLevel });
    } catch (err) {
      setCurrentData({ userInput: formattedHex, name: `Color-${formattedHex}`, palette, target: targetLevel });
    }
    setLoading(false);
    setInputHex("");
  };

  // --- [추가됨] 복사 기능 함수 ---
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    setToast(`복사 완료! ${text}`); // 메시지 띄우기

    // 2초 뒤에 메시지 사라지게 하기
    setTimeout(() => {
        setToast(null);
    }, 2000);
  };

  useEffect(() => {
    if (scrollRef.current) {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [currentData, activeProject, projects]);

  const saveToHistory = async () => {
    if (!user || !currentData) return;
    const updatedProjects = { ...projects };

    const dataToSave = { 
        id: Date.now(),
        userInput: currentData.userInput || currentData.name,
        name: currentData.name, 
        palette: currentData.palette, 
        target: currentData.target,
        isBookmarked: true 
    };

    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];
    setProjects(updatedProjects);
    setCurrentData(null);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const toggleBookmark = async (itemIndex) => {
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items[itemIndex].isBookmarked = !items[itemIndex].isBookmarked;
    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const deletePermanently = async (itemIndex) => {
    if(!window.confirm("정말 삭제하시겠습니까?")) return;
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items.splice(itemIndex, 1);
    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  }

  const handleCreateNewProject = () => {
    let baseName = "새로운 프로젝트";
    let newName = baseName;
    let count = 1;
    while (projects[newName]) {
        newName = `${baseName} ${count}`;
        count++;
    }
    const newProjects = { ...projects, [newName]: [] };
    setProjects(newProjects);
    setActiveProject(newName);
    setCurrentData(null);
    axios.post('http://localhost:5001/api/projects', { email: user.email, projects: newProjects });
  };

  const deleteProject = async (projectName, e) => {
    e.stopPropagation();
    if (!window.confirm(`'${projectName}' 채팅방을 삭제하시겠습니까?`)) return;
    const updatedProjects = { ...projects };
    delete updatedProjects[projectName];
    if (activeProject === projectName) {
        const remainingKeys = Object.keys(updatedProjects);
        if (remainingKeys.length > 0) setActiveProject(remainingKeys[0]);
        else setActiveProject("");
    }
    setProjects(updatedProjects);
    setMenuOpenId(null);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const startEditing = (projectName, e) => {
    e.stopPropagation();
    setEditingProject(projectName);
    setTempName(projectName);
    setMenuOpenId(null);
  };

  const saveRename = async (oldName) => {
    if (!tempName.trim() || tempName === oldName) {
        setEditingProject(null);
        return;
    }
    if (projects[tempName]) {
        alert("이미 존재하는 이름입니다.");
        return;
    }
    const updatedProjects = { ...projects };
    updatedProjects[tempName] = updatedProjects[oldName];
    delete updatedProjects[oldName];
    setProjects(updatedProjects);
    if (activeProject === oldName) setActiveProject(tempName);
    setEditingProject(null);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const switchProject = (projectName) => {
    if (editingProject) return; 
    setActiveProject(projectName);
    setCurrentData(null); 
  };


  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <div className="login-container">
          <div className="login-box">
            <h1>🎨 디자인 시스템 봇</h1>
            <div className="login-btn-wrapper">
                <GoogleLogin onSuccess={handleLoginSuccess} onError={() => console.log('Login Failed')} theme="filled_black" shape="pill"/>
            </div>
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  const historyList = projects[activeProject] || [];
  const displayHistory = [...historyList].reverse(); 
  const bookmarkedList = historyList.filter(item => item.isBookmarked);

  return (
    <div className="app-container">

      {/* --- [추가됨] 토스트 알림창 --- */}
      {toast && (
          <div className="toast-notification">
              <Copy size={16} /> {toast}
          </div>
      )}

      {/* 왼쪽 사이드바 */}
      <div className="sidebar">
        <div className="sidebar-top">
            <div className="logo-area">
                <h1><span style={{fontSize: '1.5rem'}}>🎨</span> 디자인 시스템 봇</h1>
            </div>
            <button onClick={handleCreateNewProject} className="new-chat-btn">
                <Plus size={18} /> 새로운 채팅
            </button>
            <div className="project-list-area">
                <h2 className="text-xs font-bold text-gray-500 mb-2 px-2">최근 활동</h2>
                <div className="project-items">
                {Object.keys(projects).map(p => (
                    <div 
                        key={p} 
                        className={`project-item-group ${activeProject === p ? 'active' : ''}`}
                        onClick={() => switchProject(p)}
                        onMouseLeave={() => setMenuOpenId(null)}
                    >
                        {editingProject === p ? (
                            <div className="rename-container" onClick={(e) => e.stopPropagation()}>
                                <input 
                                    className="rename-input"
                                    value={tempName}
                                    onChange={(e) => setTempName(e.target.value)}
                                    onKeyDown={(e) => {
                                        if (e.key === 'Enter') saveRename(p);
                                        if (e.key === 'Escape') setEditingProject(null);
                                    }}
                                    autoFocus
                                />
                                <button onClick={() => saveRename(p)} className="rename-save-btn"><Save size={14}/></button>
                            </div>
                        ) : (
                            <>
                                <div className="project-name-wrapper">
                                    <Folder size={16} className={activeProject === p ? "text-white" : "text-gray-500"} /> 
                                    <span className="truncate">{p}</span>
                                </div>
                                <div className="project-actions">
                                    <button 
                                        className="action-btn"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            setMenuOpenId(menuOpenId === p ? null : p);
                                        }}
                                    >
                                        <MoreHorizontal size={16}/>
                                    </button>
                                    {menuOpenId === p && (
                                        <div className="dropdown-menu">
                                            <button onClick={(e) => startEditing(p, e)}>
                                                <Edit3 size={14}/> 이름 변경
                                            </button>
                                            <button onClick={(e) => deleteProject(p, e)} className="delete-opt">
                                                <Trash2 size={14}/> 삭제
                                            </button>
                                        </div>
                                    )}
                                </div>
                            </>
                        )}
                    </div>
                ))}
                </div>
            </div>
        </div>

        <div className="user-profile">
            <div className="user-info">
                {user.picture ? <img src={user.picture} alt="profile" /> : <div className="user-avatar-placeholder"><User size={16}/></div>}
                <div className="user-text">
                    <p className="user-name">{user.name}</p>
                    <p className="user-email">{user.email}</p>
                </div>
            </div>
            <button onClick={handleLogout} className="logout-btn">로그아웃</button>
        </div>
      </div>

      {/* 중앙 메인 영역 */}
      <div className="main-content">
        <main className="chat-area">
            {displayHistory.length === 0 && !currentData && (
                <div className="welcome-screen">
                    <h2>안녕하세요 {user.name}님,<br/>
                    <span className="highlight-text">'{activeProject}' 대화방입니다.</span>
                    </h2>
                </div>
            )}

            {displayHistory.map((item, idx) => {
                const originalIndex = historyList.length - 1 - idx;
                return (
                <div key={item.id || idx} className="history-item-group">
                    <div className="user-message">
                        <div className="bubble">{item.userInput || item.name}</div>
                    </div>
                    <div className="bot-response">
                        <div className="bot-avatar">🤖</div>
                        <div className="response-card" style={{borderColor: item.isBookmarked ? '#3b82f6' : '#374151'}}>
                             <div className="card-header">
                                <div className="tag-row">
                                    {item.isBookmarked ? 
                                        <span className="ai-tag bg-blue-900 text-blue-200 flex items-center gap-1"><Star size={12} fill="currentColor"/> 보관됨</span> :
                                        <span className="ai-tag bg-gray-700 text-gray-400">기록됨</span>
                                    }
                                    <h3 className="text-xl font-bold text-white ml-2">{item.name}</h3>
                                </div>
                                <div className="flex gap-2">
                                    <button onClick={() => toggleBookmark(originalIndex)} className="text-gray-400 hover:text-yellow-400 transition-colors">
                                        {item.isBookmarked ? <Star size={18} fill="#facc15" className="text-yellow-400"/> : <Star size={18} />}
                                    </button>
                                    <button onClick={() => deletePermanently(originalIndex)} className="text-gray-400 hover:text-red-500 transition-colors">
                                        <Trash2 size={18}/>
                                    </button>
                                </div>
                            </div>
                            <div className="palette-grid">
                                {item.palette.map((color, i) => (
                                <div key={i} className="color-item">
                                    {/* --- [수정] 클릭 시 복사 기능 추가 --- */}
                                    <div 
                                        className={`color-box ${color.isTarget ? 'is-target' : ''}`} 
                                        style={{ backgroundColor: color.hex }}
                                        onClick={() => copyToClipboard(color.hex)}
                                        title="클릭해서 복사하기"
                                    ></div>
                                    <p className="level-text">{color.level}</p>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )})}

            {currentData && (
                <div className="current-generation-group animate-fade-in-up">
                    <div className="user-message">
                        <div className="bubble">{currentData.userInput} 만들어줘</div>
                    </div>
                    <div className="bot-response" ref={scrollRef}>
                        <div className="bot-avatar" style={{filter: 'hue-rotate(90deg)'}}>✨</div>
                        <div className="response-card" style={{border: '1px solid #3b82f6'}}>
                            <div className="card-header">
                                <div>
                                    <div className="tag-row"><span className="ai-tag">New Generation</span></div>
                                    <input className="naming-input" value={currentData.name} onChange={(e) => setCurrentData({...currentData, name: e.target.value})}/>
                                </div>
                                <button onClick={saveToHistory} className="save-btn"><Save size={16} /> 대화에 추가하기</button>
                            </div>
                            <div className="palette-grid">
                                {currentData.palette.map((color, idx) => (
                                <div key={idx} className="color-item">
                                    {/* --- [수정] 클릭 시 복사 기능 추가 --- */}
                                    <div 
                                        className={`color-box ${color.isTarget ? 'is-target' : ''}`} 
                                        style={{ backgroundColor: color.hex }}
                                        onClick={() => copyToClipboard(color.hex)}
                                        title="클릭해서 복사하기"
                                    >
                                    {color.isTarget && <span className="target-dot"></span>}
                                    </div>
                                    <p className={`level-text ${color.isTarget ? 'target-text' : ''}`}>{color.level}</p>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )}
            <div style={{height: '100px'}}></div>
        </main>

        <div className="input-area">
            <form onSubmit={handleGenerate} className="input-form">
                <input placeholder="HEX 코드를 입력하세요 (예: #3A86FF)" value={inputHex} onChange={(e) => setInputHex(e.target.value)} disabled={loading}/>
                <button type="submit" disabled={loading || !inputHex} className={loading || !inputHex ? 'disabled' : ''}>
                    {loading ? <div className="spinner"/> : <Send size={20} />}
                </button>
            </form>
        </div>
      </div>

      <div className="vault-sidebar">
          <h3><Star size={20} style={{color: '#facc15', fill:'#facc15'}}/> 즐겨찾기 보관함</h3>
          <div className="vault-list">
            {bookmarkedList.length === 0 ? <p className="empty-msg">보관된 아이템이 없습니다.</p> : 
             bookmarkedList.map((item, idx) => {
                const realIndex = historyList.indexOf(item);
                return (
                <div key={idx} className="vault-item">
                    <div className="vault-header">
                        <h4>{item.name}</h4>
                        <button onClick={() => toggleBookmark(realIndex)} className="delete-btn"><StarOff size={14}/></button>
                    </div>
                    <div className="mini-palette">
                         {item.palette.slice(0,5).map((p, i) => <div key={i} style={{backgroundColor: p.hex, flex: 1}}></div>)}
                    </div>
                </div>
            )})}
          </div>
      </div>
    </div>
  );
}

export default App;
