import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Download, Plus, Save } from 'lucide-react'; // ÏïÑÏù¥ÏΩò

// Íµ¨Í∏Ä ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ ID
const CLIENT_ID = "YOUR_GOOGLE_CLIENT_ID"; 

function App() {
  const [user, setUser] = useState(null);
  const [projects, setProjects] = useState({ "SOOP ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú": [] });
  const [activeProject, setActiveProject] = useState("SOOP ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú");
  const [inputHex, setInputHex] = useState("");
  const [currentData, setCurrentData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [newProjectName, setNewProjectName] = useState("");

  // 1. Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/projects/${email}`);
      setProjects(res.data);
    } catch (err) { console.error(err); }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setProjects({ "SOOP ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú": [] });
  };

  // 2. ÏÉâÏÉÅ ÏÉùÏÑ± & AI ÎÑ§Ïù¥Î∞ç
  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex.startsWith("#")) return;

    setLoading(true);
    const { palette, targetLevel } = calculatePalette(inputHex);

    try {
      const res = await axios.post('http://localhost:5000/api/ai-naming', { hex: inputHex });
      setCurrentData({ name: res.data.name, palette, target: targetLevel });
    } catch (err) {
      setCurrentData({ name: `Color-${inputHex}`, palette, target: targetLevel });
    }
    setLoading(false);
  };

  // 3. Ï†ÄÏû•
  const saveToVault = async () => {
    if (!user || !currentData) return;

    const updatedProjects = { ...projects };
    updatedProjects[activeProject] = [currentData, ...(updatedProjects[activeProject] || [])];

    setProjects(updatedProjects);
    await axios.post('http://localhost:5000/api/projects', {
      email: user.email,
      projects: updatedProjects
    });
    alert("Ï†ÄÏû• ÏôÑÎ£å!");
  };

  // 4. ÌîÑÎ°úÏ†ùÌä∏ Ï∂îÍ∞Ä
  const addProject = () => {
    if (!newProjectName) return;
    setProjects({ ...projects, [newProjectName]: [] });
    setNewProjectName("");
  };

  // --- Î†åÎçîÎßÅ ---
  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <div className="flex h-screen items-center justify-center bg-gray-100">
          <div className="bg-white p-8 rounded-xl shadow-lg text-center">
            <h1 className="text-2xl font-bold mb-6">üé® ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú Î¥á</h1>
            <GoogleLogin onSuccess={handleLoginSuccess} onError={() => console.log('Login Failed')} />
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  return (
    <div className="flex h-screen bg-white">
      {/* --- ÏÇ¨Ïù¥ÎìúÎ∞î --- */}
      <div className="w-64 bg-gray-50 border-r p-4 flex flex-col">
        <h2 className="text-xl font-bold mb-4">üìÅ ÌîÑÎ°úÏ†ùÌä∏</h2>
        <div className="flex gap-2 mb-4">
          <input 
            className="border p-2 rounded w-full text-sm" 
            placeholder="ÏÉà ÌîÑÎ°úÏ†ùÌä∏" 
            value={newProjectName}
            onChange={(e) => setNewProjectName(e.target.value)}
          />
          <button onClick={addProject} className="bg-blue-500 text-white p-2 rounded"><Plus size={16}/></button>
        </div>

        <div className="flex-1 overflow-y-auto">
          {Object.keys(projects).map(p => (
            <button
              key={p}
              onClick={() => setActiveProject(p)}
              className={`w-full text-left p-2 rounded mb-1 ${activeProject === p ? 'bg-blue-100 text-blue-600 font-bold' : 'hover:bg-gray-100'}`}
            >
              {p}
            </button>
          ))}
        </div>

        <div className="mt-4 border-t pt-4">
          <p className="text-sm text-gray-500 mb-2">{user.name}Îãò</p>
          <button onClick={handleLogout} className="text-red-500 text-sm">Î°úÍ∑∏ÏïÑÏõÉ</button>
        </div>
      </div>

      {/* --- Î©îÏù∏ ÏòÅÏó≠ --- */}
      <div className="flex-1 flex">
        <div className="flex-1 p-10 overflow-y-auto">
          <h1 className="text-3xl font-bold mb-2">üé® {activeProject}</h1>
          <p className="text-gray-500 mb-8">AIÏôÄ Ìï®Íªò ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú Ïª¨Îü¨Î•º ÏÉùÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî.</p>

          <form onSubmit={handleGenerate} className="mb-8 flex gap-2">
            <input 
              className="border-2 border-gray-300 p-3 rounded-lg w-full max-w-md text-lg"
              placeholder="#3A86FF"
              value={inputHex}
              onChange={(e) => setInputHex(e.target.value)}
            />
            <button type="submit" disabled={loading} className="bg-black text-white px-6 rounded-lg font-bold">
              {loading ? "ÏÉùÏÑ± Ï§ë..." : "ÏÉùÏÑ±"}
            </button>
          </form>

          {currentData && (
            <div className="bg-white p-6 rounded-xl border shadow-sm">
              <div className="flex justify-between items-center mb-4">
                <div>
                  <span className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded">Target: {currentData.target}</span>
                  <input 
                    className="block text-2xl font-bold mt-1 border-b border-transparent hover:border-gray-300 focus:outline-none"
                    value={currentData.name}
