import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Plus, Save, User, Send, Folder, MoreHorizontal, Edit3, Star, StarOff, Copy, Loader2 } from 'lucide-react'; 
import './App.css'; 

const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('designBotUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [projects, setProjects] = useState({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
  const [activeProject, setActiveProject] = useState("ê¸°ë³¸ í”„ë¡œì íŠ¸");
  const [inputHex, setInputHex] = useState("");
  const [loading, setLoading] = useState(false); // ë¡œë”© ìƒíƒœ
  const scrollRef = useRef(null);

  const [toast, setToast] = useState(null);
  const [menuOpenId, setMenuOpenId] = useState(null);
  const [editingProject, setEditingProject] = useState(null);
  const [tempName, setTempName] = useState("");

  useEffect(() => {
    if (user && user.email) {
      fetchUserData(user.email);
    }
  }, [user]);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    localStorage.setItem('designBotUser', JSON.stringify(decoded)); 
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5001/api/projects/${email}`);
      setProjects(res.data || { "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    localStorage.removeItem('designBotUser'); 
  };

  // ğŸ”¥ [í•µì‹¬ ë³€ê²½] ìƒì„± ì¦‰ì‹œ ì €ì¥í•˜ëŠ” í•¨ìˆ˜
  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("ì˜¬ë°”ë¥¸ HEX ì½”ë“œë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš” (ì˜ˆ: #3A86FF)");
        return;
    }

    setLoading(true); // ë¡œë”© ì‹œì‘! (í™”ë©´ì— 'ìƒê° ì¤‘...' í‘œì‹œ)

    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);
    let aiName = `Color-${formattedHex}`;

    try {
      // AIì—ê²Œ ì´ë¦„ ë¬¼ì–´ë³´ê¸° (ì‹œê°„ì´ ì¢€ ê±¸ë¦¼)
      const res = await axios.post('http://localhost:5001/api/ai-naming', { hex: formattedHex });
      aiName = res.data.name;
    } catch (err) {
      console.error("AI Naming Failed", err);
    }

    // --- [ì—¬ê¸°ì„œ ë°”ë¡œ ì €ì¥!] ---
    const updatedProjects = { ...projects };

    const dataToSave = { 
        id: Date.now(),
        userInput: formattedHex,
        name: aiName, 
        palette: palette, 
        target: targetLevel,
        isBookmarked: false 
    };

    // ìµœì‹ ìˆœ(ë°°ì—´ ì•)ì— ì¶”ê°€
    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];

    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });

    setLoading(false); // ë¡œë”© ë!
    setInputHex("");   // ì…ë ¥ì°½ ë¹„ìš°ê¸°
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    setToast(`ë³µì‚¬ ì™„ë£Œ! ${text}`);
    setTimeout(() => setToast(null), 2000);
  };

  // ìŠ¤í¬ë¡¤ ìë™ ì´ë™ (ìƒˆ ë©”ì‹œì§€ë‚˜ ë¡œë”© ì‹œ)
  useEffect(() => {
    if (scrollRef.current) {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [projects, loading, activeProject]);

  const toggleBookmark = async (itemIndex) => {
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items[itemIndex].isBookmarked = !items[itemIndex].isBookmarked;
    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const deletePermanently = async (itemIndex) => {
    if(!window.confirm("ì •ë§ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) return;
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items.splice(itemIndex, 1);
    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  }

  const handleCreateNewProject = () => {
    let baseName = "ìƒˆë¡œìš´ í”„ë¡œì íŠ¸";
    let newName = baseName;
    let count = 1;
    while (projects[newName]) {
        newName = `${baseName} ${count}`;
        count++;
    }
    const newProjects = { ...projects, [newName]: [] };
    setProjects(newProjects);
    setActiveProject(newName);
    axios.post('http://localhost:5001/api/projects', { email: user.email, projects: newProjects });
  };

  const deleteProject = async (projectName, e) => {
    e.stopPropagation();
    if (!window.confirm(`'${projectName}' ì±„íŒ…ë°©ì„ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?`)) return;
    const updatedProjects = { ...projects };
    delete updatedProjects[projectName];
    if (activeProject === projectName) {
        const remainingKeys = Object.keys(updatedProjects);
        if (remainingKeys.length > 0) setActiveProject(remainingKeys[0]);
        else setActiveProject("");
    }
    setProjects(updatedProjects);
    setMenuOpenId(null);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const startEditing = (projectName, e) => {
    e.stopPropagation();
    setEditingProject(projectName);
    setTempName(projectName);
    setMenuOpenId(null);
  };

  const saveRename = async (oldName) => {
    if (!tempName.trim() || tempName === oldName) {
        setEditingProject(null);
        return;
    }
    if (projects[tempName]) {
        alert("ì´ë¯¸ ì¡´ì¬í•˜ëŠ” ì´ë¦„ì…ë‹ˆë‹¤.");
        return;
    }
    const updatedProjects = { ...projects };
    updatedProjects[tempName] = updatedProjects[oldName];
    delete updatedProjects[oldName];
    setProjects(updatedProjects);
    if (activeProject === oldName) setActiveProject(tempName);
    setEditingProject(null);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const switchProject = (projectName) => {
    if (editingProject) return; 
    setActiveProject(projectName);
  };

  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <div className="login-container">
          <div className="login-box">
            <h1>ğŸ¨ ë””ìì¸ ì‹œìŠ¤í…œ ë´‡</h1>
            <div className="login-btn-wrapper">
                <GoogleLogin onSuccess={handleLoginSuccess} onError={() => console.log('Login Failed')} theme="filled_black" shape="pill"/>
            </div>
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  const historyList = projects[activeProject] || [];
  const displayHistory = [...historyList].reverse(); 
  const bookmarkedList = historyList.filter(item => item.isBookmarked);

  return (
    <div className="app-container">
      {toast && <div className="toast-notification"><Copy size={16} /> {toast}</div>}

      {/* ì™¼ìª½ ì‚¬ì´ë“œë°” */}
      <div className="sidebar">
        <div className="sidebar-top">
            <div className="logo-area"><h1><span style={{fontSize: '1.5rem'}}>ğŸ¨</span> ë””ìì¸ ì‹œìŠ¤í…œ ë´‡</h1></div>
            <button onClick={handleCreateNewProject} className="new-chat-btn"><Plus size={18} /> ìƒˆë¡œìš´ ì±„íŒ…</button>
            <div className="project-list-area">
                <h2 className="text-xs font-bold text-gray-500 mb-2 px-2">ìµœê·¼ í™œë™</h2>
                <div className="project-items">
                {Object.keys(projects).map(p => (
                    <div 
                        key={p} 
                        className={`project-item-group ${activeProject === p ? 'active' : ''}`}
                        onClick={() => switchProject(p)}
                        onMouseLeave={() => setMenuOpenId(null)}
                    >
                        {editingProject === p ? (
                            <div className="rename-container" onClick={(e) => e.stopPropagation()}>
                                <input className="rename-input" value={tempName} onChange={(e) => setTempName(e.target.value)} onKeyDown={(e) => {if (e.key === 'Enter') saveRename(p); if (e.key === 'Escape') setEditingProject(null);}} autoFocus />
                                <button onClick={() => saveRename(p)} className="rename-save-btn"><Save size={14}/></button>
                            </div>
                        ) : (
                            <>
                                <div className="project-name-wrapper">
                                    <Folder size={16} className={activeProject === p ? "text-white" : "text-gray-500"} /> 
                                    <span className="truncate">{p}</span>
                                </div>
                                <div className="project-actions">
                                    <button className="action-btn" onClick={(e) => {e.stopPropagation(); setMenuOpenId(menuOpenId === p ? null : p);}}><MoreHorizontal size={16}/></button>
                                    {menuOpenId === p && (
                                        <div className="dropdown-menu">
                                            <button onClick={(e) => startEditing(p, e)}><Edit3 size={14}/> ì´ë¦„ ë³€ê²½</button>
                                            <button onClick={(e) => deleteProject(p, e)} className="delete-opt"><Trash2 size={14}/> ì‚­ì œ</button>
                                        </div>
                                    )}
                                </div>
                            </>
                        )}
                    </div>
                ))}
                </div>
            </div>
        </div>
        <div className="user-profile">
            <div className="user-info">
                {user.picture ? <img src={user.picture} alt="profile" /> : <div className="user-avatar-placeholder"><User size={16}/></div>}
                <div className="user-text"><p className="user-name">{user.name}</p><p className="user-email">{user.email}</p></div>
            </div>
            <button onClick={handleLogout} className="logout-btn">ë¡œê·¸ì•„ì›ƒ</button>
        </div>
      </div>

      {/* ì¤‘ì•™ ë©”ì¸ ì˜ì—­ */}
      <div className="main-content">
        <main className="chat-area">
            {displayHistory.length === 0 && !loading && (
                <div className="welcome-screen">
                    <h2>ì•ˆë…•í•˜ì„¸ìš” {user.name}ë‹˜,<br/><span className="highlight-text">'{activeProject}' ëŒ€í™”ë°©ì…ë‹ˆë‹¤.</span></h2>
                </div>
            )}

            {displayHistory.map((item, idx) => {
                const originalIndex = historyList.length - 1 - idx;
                return (
                <div key={item.id || idx} className="history-item-group animate-fade-in-up">
                    <div className="user-message">
                        <div className="bubble">{item.userInput || item.name}</div>
                    </div>
                    <div className="bot-response">
                        <div className="bot-avatar">ğŸ¤–</div>
                        <div className="response-card" style={{borderColor: item.isBookmarked ? '#3b82f6' : '#374151'}}>
                             <div className="card-header">
                                <div className="tag-row">
                                    {item.isBookmarked ? 
                                        <span className="ai-tag bg-blue-900 text-blue-200 flex items-center gap-1"><Star size={12} fill="currentColor"/> ë³´ê´€ë¨</span> :
                                        <span className="ai-tag bg-gray-700 text-gray-400">AI ì¶”ì²œ ë„¤ì´ë°</span>
                                    }
                                    <h3 className="text-xl font-bold text-white ml-2">{item.name}</h3>
                                </div>
                                <div className="flex gap-2">
                                    <button onClick={() => toggleBookmark(originalIndex)} className="text-gray-400 hover:text-yellow-400 transition-colors">
                                        {item.isBookmarked ? <Star size={18} fill="#facc15" className="text-yellow-400"/> : <Star size={18} />}
                                    </button>
                                    <button onClick={() => deletePermanently(originalIndex)} className="text-gray-400 hover:text-red-500 transition-colors">
                                        <Trash2 size={18}/>
                                    </button>
                                </div>
                            </div>
                            <div className="palette-grid">
                                {item.palette.map((color, i) => (
                                <div key={i} className="color-item">
                                    <div className={`color-box ${color.isTarget ? 'is-target' : ''}`} style={{ backgroundColor: color.hex }} onClick={() => copyToClipboard(color.hex)} title="í´ë¦­í•´ì„œ ë³µì‚¬í•˜ê¸°">
                                        {color.isTarget && <span className="target-dot"></span>}
                                    </div>
                                    <p className="level-text">{color.level}</p>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )})}

