import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Plus, Save, User, Send, Folder, MoreHorizontal, Edit3, Star, StarOff, Copy, Loader2, X } from 'lucide-react'; 
import './App.css'; 

const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('designBotUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [projects, setProjects] = useState({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
  const [activeProject, setActiveProject] = useState("Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏");
  const [inputHex, setInputHex] = useState("");
  const [loading, setLoading] = useState(false); 
  const [showSpacingOptions, setShowSpacingOptions] = useState(false); // üî• Ïä§ÌéòÏù¥Ïã± ÏòµÏÖò ÏÉÅÌÉú
  const scrollRef = useRef(null);
  const [toast, setToast] = useState(null);

  const [menuOpenId, setMenuOpenId] = useState(null);
  const [editingProject, setEditingProject] = useState(null);
  const [tempName, setTempName] = useState("");

  useEffect(() => {
    if (user && user.email) {
      fetchUserData(user.email);
    }
  }, [user]);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    localStorage.setItem('designBotUser', JSON.stringify(decoded)); 
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5001/api/projects/${email}`);
      setProjects(res.data || { "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setProjects({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    localStorage.removeItem('designBotUser'); 
  };

  // üî• [ÌïµÏã¨] ÏûÖÎ†•Ï∞Ω Ï≤òÎ¶¨ Ìï®Ïàò
  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;

    // 1. Ïä§ÌéòÏù¥Ïã± ÌÇ§ÏõåÎìú Ï≤¥ÌÅ¨ (Í∞ÄÏû• Î®ºÏ†Ä Ïã§Ìñâ)
    if (inputHex.toLowerCase().includes("spacing") || inputHex.includes("Ïä§ÌéòÏù¥Ïã±")) {
        console.log("Spacing ÏöîÏ≤≠ Í∞êÏßÄ!");
        setShowSpacingOptions(true); // Î≤ÑÌäº ÌëúÏãú
        setInputHex("");
        return;
    }

    // 2. Ïª¨Îü¨ HEX Ï≤¥ÌÅ¨
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("Ïò¨Î∞îÎ•∏ HEX ÏΩîÎìú ÎòêÎäî 'Spacing'ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
        return;
    }

    setLoading(true);
    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);
    let aiName = `Color-${formattedHex}`;

    try {
      const res = await axios.post('http://localhost:5001/api/ai-naming', { hex: formattedHex });
      aiName = res.data.name;
    } catch (err) {
      console.error("AI Naming Failed", err);
    }

    const updatedProjects = { ...projects };
    const dataToSave = { 
        id: Date.now(),
        userInput: formattedHex,
        name: aiName, 
        palette: palette, 
        target: targetLevel,
        isBookmarked: false,
        type: 'color'
    };

    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });

    setLoading(false); 
    setInputHex("");   
  };

  // üî• [ÌïµÏã¨] Ïä§ÌéòÏù¥Ïã± ÌÜ†ÌÅ∞ ÏÉùÏÑ±
  const generateSpacingTokens = async (type) => {
    setLoading(true);
    setShowSpacingOptions(false);

    let maxStep = 9;
    if (type === 'tablet') maxStep = 12;
    if (type === 'pc') maxStep = 15;

    const spacingPalette = [];
    spacingPalette.push({ level: 'sp0.5', value: 2, isVisible: true });

    for (let i = 1; i <= maxStep; i++) {
      spacingPalette.push({ level: `sp${i}`, value: i * 4, isVisible: true });
    }

    if (type === 'pc') {
      spacingPalette.push({ level: 'sp20', value: 80, isVisible: true });
      spacingPalette.push({ level: 'sp25', value: 100, isVisible: true });
    }

    const updatedProjects = { ...projects };
    const dataToSave = { 
        id: Date.now(),
        userInput: `Spacing (${type})`,
        name: `${type.toUpperCase()} Spacing`, 
        spacingPalette: spacingPalette, 
        type: 'spacing',
        isBookmarked: false 
    };

    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
    setLoading(false);
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    setToast(`Î≥µÏÇ¨ ÏôÑÎ£å! ${text}`);
    setTimeout(() => setToast(null), 2000);
  };

  const toggleBookmark = async (itemIndex) => {
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items[itemIndex].isBookmarked = !items[itemIndex].isBookmarked;
    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const toggleColorVisibility = async (itemIndex, colorIndex) => {
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    const targetItem = items[itemIndex];

    if (targetItem.type === 'spacing') {
        targetItem.spacingPalette[colorIndex].isVisible = !targetItem.spacingPalette[colorIndex].isVisible;
    } else {
        targetItem.palette[colorIndex].isVisible = !targetItem.palette[colorIndex].isVisible;
    }

    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', { email: user.email, projects: updatedProjects });
  };

  const switchProject = (projectName) => {
    if (editingProject) return; 
    setActiveProject(projectName);
  };

  const historyList = projects[activeProject] || [];
  const displayHistory = [...historyList].reverse(); 
  const bookmarkedList = historyList.filter(item => item.isBookmarked);

  return (
    <div className="app-container">
      {toast && <div className="toast-notification"><Copy size={16} /> {toast}</div>}

      <div className="sidebar">
        <div className="sidebar-top">
            <div className="logo-area"><h1>üé® ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú Î¥á</h1></div>
            <button onClick={() => {}} className="new-chat-btn"><Plus size={18} /> ÏÉàÎ°úÏö¥ Ï±ÑÌåÖ</button>
            <div className="project-list-area">
                <div className="list-title">ÎÇòÏùò ÎîîÏûêÏù∏ÏãúÏä§ÌÖú</div>
                <div className="project-items">
                {Object.keys(projects).map(p => (
                    <div key={p} className={`project-item-group ${activeProject === p ? 'active' : ''}`} onClick={() => switchProject(p)}>
                        <Folder size={16} /> <span>{p}</span>
                    </div>
                ))}
                </div>
            </div>
        </div>
        <div className="user-profile">
            <div className="user-info">
                {user.picture && <img src={user.picture} alt="p" />}
                <div className="user-text"><p>{user.name}</p></div>
            </div>
            <button onClick={handleLogout} className="logout-btn">Î°úÍ∑∏ÏïÑÏõÉ</button>
        </div>
      </div>

      <div className="main-content">
        <main className="chat-area">
            {displayHistory.map((item, idx) => {
                const originalIndex = historyList.length - 1 - idx;
                return (
                <div key={item.id || idx} className="history-item-group">
                    <div className="user-message"><div className="bubble">{item.userInput}</div></div>
                    <div className="bot-response">
                        <div className="bot-avatar">ü§ñ</div>
                        <div className="response-card">
                            <div className="card-header">
                                <h3>{item.name}</h3>
                                <button onClick={() => toggleBookmark(originalIndex)}>
                                    <Star size={18} fill={item.isBookmarked ? "#facc15" : "none"} color={item.isBookmarked ? "#facc15" : "#6b7280"}/>
                                </button>
                            </div>

                            {/* üî• Ïä§ÌéòÏù¥Ïã± Î†åÎçîÎßÅ */}
                            {item.type === 'spacing' ? (
                                <div className="spacing-grid">
                                    {item.spacingPalette.map((sp, i) => (
                                        <div key={i} className="spacing-item" onClick={() => copyToClipboard(`${sp.value}px`)}>
                                            <div className="spacing-box" style={{width: `${sp.value}px`, height: '12px'}}></div>
                                            <p className="sp-label">{sp.level}</p>
                                        </div>
                                    ))}
                                </div>
                            ) : (
                                <div className="palette-grid">
                                    {item.palette.map((color, i) => (
                                        <div key={i} className="color-item">
                                            <div className="color-box" style={{ backgroundColor: color.hex }} onClick={() => copyToClipboard(color.hex)}></div>
                                            <p className="level-text">{color.level}</p>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )})}

            {/* üî• [Ïù¥ÎØ∏ÏßÄ Íµ¨ÌòÑ] Ïä§ÌéòÏù¥Ïã± Î≤ÑÌäº ÏòµÏÖò */}
            {showSpacingOptions && (
                <div className="bot-response animate-fade-in-up">
                    <div className="bot-avatar">ü§ñ</div>
                    <div className="response-card spacing-selection-card">
                        <p className="mb-4">Ïñ¥Îñ§ ÌîÑÎ°úÏ†ùÌä∏ Ïù∏Í∞ÄÏöî?</p>
                        <div className="flex gap-3">
                            <button className="option-btn" onClick={() => generateSpacingTokens('pc')}>PC</button>
                            <button className="option-btn" onClick={() => generateSpacingTokens('tablet')}>Tablet</button>
                            <button className="option-btn" onClick={() => generateSpacingTokens('mobile')}>Mobile</button>
                        </div>
                    </div>
                </div>
            )}

            {loading && <div className="loading-bubble">ÏÉùÍ∞Å Ï§ë...</div>}
            <div ref={scrollRef} style={{height: '1px'}}></div>
        </main>

        <div className="input-area">
            <form onSubmit={handleGenerate} className="input-form">
                <input placeholder="HEX ÏΩîÎìú ÎòêÎäî 'Spacing' ÏûÖÎ†•" value={inputHex} onChange={(e) => setInputHex(e.target.value)} />
                <button type="submit"><Send size={20} /></button>
            </form>
        </div>
      </div>

      {/* Ïò§Î•∏Ï™Ω Î≥¥Í¥ÄÌï® (Ïª¨Îü¨ + Ïä§ÌéòÏù¥Ïã± ÎåÄÏùë) */}
      <div className="vault-sidebar">
          <h3>ÎÇ¥ Î≥¥Í¥ÄÌï®</h3>
          <div className="vault-list">
            {bookmarkedList.map((item, idx) => {
                const realIndex = historyList.indexOf(item);
                return (
                <div key={idx} className="vault-item">
                    <div className="vault-header"><h4>{item.name}</h4><button onClick={() => toggleBookmark(realIndex)} className="vault-remove-btn">Ï†úÍ±∞</button></div>
                    <div className="vault-palette-grid">
                         {(item.type === 'spacing' ? item.spacingPalette : item.palette).map((chip, cIdx) => (
                                <div key={cIdx} className="vault-chip-wrapper">
                                    {chip.isVisible !== false ? (
                                        <div className="vault-chip" style={{backgroundColor: item.type === 'spacing' ? '#6366f1' : chip.hex}} onClick={() => copyToClipboard(item.type === 'spacing' ? `${chip.value}px` : chip.hex)}>
                                            <div className="vault-chip-overlay" onClick={(e) => {e.stopPropagation(); toggleColorVisibility(realIndex, cIdx);}}><X size={14} color="white"/></div>
                                        </div>
                                    ) : (
                                        <button className="vault-chip-restore" onClick={() => toggleColorVisibility(realIndex, cIdx)}><Plus size={14} /></button>
                                    )}
                                    <span className="vault-chip-label">{chip.level}</span>
                                </div>
                         ))}
                    </div>
                </div>
            )})}
          </div>
      </div>
    </div>
  );
}
export default App;
