import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Plus, Save, User, Send, Folder, MessageSquarePlus, Star, StarOff } from 'lucide-react'; 
import './App.css'; 

const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('designBotUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [projects, setProjects] = useState({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
  const [activeProject, setActiveProject] = useState("ê¸°ë³¸ í”„ë¡œì íŠ¸");
  const [inputHex, setInputHex] = useState("");

  // í˜„ì¬ ìƒì„± ì¤‘ì¸(ì €ì¥ ì „) ë°ì´í„°
  const [currentData, setCurrentData] = useState(null);

  const [loading, setLoading] = useState(false);
  const [newProjectName, setNewProjectName] = useState("");
  const scrollRef = useRef(null);

  useEffect(() => {
    if (user && user.email) {
      fetchUserData(user.email);
    }
  }, [user]);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    localStorage.setItem('designBotUser', JSON.stringify(decoded)); 
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5001/api/projects/${email}`);
      setProjects(res.data || { "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setCurrentData(null);
    setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    localStorage.removeItem('designBotUser'); 
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("ì˜¬ë°”ë¥¸ HEX ì½”ë“œë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš” (ì˜ˆ: #3A86FF)");
        return;
    }

    setLoading(true);
    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);

    try {
      const res = await axios.post('http://localhost:5001/api/ai-naming', { hex: formattedHex });
      setCurrentData({ userInput: formattedHex, name: res.data.name, palette, target: targetLevel });
    } catch (err) {
      setCurrentData({ userInput: formattedHex, name: `Color-${formattedHex}`, palette, target: targetLevel });
    }
    setLoading(false);
    setInputHex("");
  };

  useEffect(() => {
    if (scrollRef.current) {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [currentData, activeProject, projects]);

  // ğŸ”¥ [í•µì‹¬ 1] ì €ì¥: íˆìŠ¤í† ë¦¬ì— ì¶”ê°€ + ì¦ê²¨ì°¾ê¸°(isBookmarked) True ì„¤ì •
  const saveToHistory = async () => {
    if (!user || !currentData) return;
    const updatedProjects = { ...projects };

    const dataToSave = { 
        id: Date.now(), // ê³ ìœ  ID ë¶€ì—¬
        userInput: currentData.userInput || currentData.name,
        name: currentData.name, 
        palette: currentData.palette, 
        target: currentData.target,
        isBookmarked: true // ì²˜ìŒ ì €ì¥í•  ë• ë³´ê´€í•¨ì—ë„ ë³´ì´ê²Œ ì„¤ì •
    };

    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];

    setProjects(updatedProjects);
    setCurrentData(null); // ì…ë ¥ì°½ ë¹„ìš°ê¸° (ëŒ€í™” ì™„ë£Œ ì²˜ë¦¬)

    await axios.post('http://localhost:5001/api/projects', {
      email: user.email,
      projects: updatedProjects
    });
  };

  // ğŸ”¥ [í•µì‹¬ 2] ë³´ê´€í•¨(ì¦ê²¨ì°¾ê¸°) í† ê¸€ í•¨ìˆ˜
  // ì˜¤ë¥¸ìª½ì—ì„œ ì‚­ì œë¥¼ ëˆŒëŸ¬ë„ 'ë°ì´í„° ì‚­ì œ'ê°€ ì•„ë‹ˆë¼ 'ë¶ë§ˆí¬ í•´ì œ'ë§Œ í•©ë‹ˆë‹¤.
  const toggleBookmark = async (itemIndex) => {
    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];

    // true -> false (ë³´ê´€í•¨ ì‚­ì œ), false -> true (ë³´ê´€í•¨ ì¶”ê°€)
    items[itemIndex].isBookmarked = !items[itemIndex].isBookmarked;

    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);

    await axios.post('http://localhost:5001/api/projects', {
        email: user.email,
        projects: updatedProjects
    });
  };

  // ì•„ì˜ˆ ì˜êµ¬ ì‚­ì œ (íˆìŠ¤í† ë¦¬ì—ì„œë„ ì§€ìš°ê¸°)
  const deletePermanently = async (itemIndex) => {
    if(!window.confirm("ì •ë§ ëŒ€í™” ë‚´ì—­ì—ì„œ ì™„ì „íˆ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) return;

    const updatedProjects = { ...projects };
    const items = [...updatedProjects[activeProject]];
    items.splice(itemIndex, 1);

    updatedProjects[activeProject] = items;
    setProjects(updatedProjects);

    await axios.post('http://localhost:5001/api/projects', {
        email: user.email,
        projects: updatedProjects
    });
  }

  const addProject = () => {
    if (!newProjectName) return;
    if (projects[newProjectName]) {
        alert("ì´ë¯¸ ì¡´ì¬í•˜ëŠ” í”„ë¡œì íŠ¸ ì´ë¦„ì…ë‹ˆë‹¤.");
        return;
    }
    const newProjects = { ...projects, [newProjectName]: [] };
    setProjects(newProjects);
    setActiveProject(newProjectName);
    setCurrentData(null); 
    setNewProjectName("");
  };

  const switchProject = (projectName) => {
    setActiveProject(projectName);
    setCurrentData(null); 
  };

  // ğŸ”¥ [í•µì‹¬ 3] ìƒˆ ì±„íŒ…: í™”ë©´ ë¹„ìš°ê¸° ì—­í• 
  const startNewChat = () => {
    setCurrentData(null);
    setInputHex("");
  };


  // --- ë Œë”ë§ ---

  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <div className="login-container">
          <div className="login-box">
            <h1>ğŸ¨ ë””ìì¸ ì‹œìŠ¤í…œ ë´‡</h1>
            <div className="login-btn-wrapper">
                <GoogleLogin onSuccess={handleLoginSuccess} onError={() => console.log('Login Failed')} theme="filled_black" shape="pill"/>
            </div>
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  // ì „ì²´ íˆìŠ¤í† ë¦¬ (ì±„íŒ…ì°½ì— ë³´ì—¬ì§ˆ ë‚´ìš©) - ìµœì‹ ìˆœ ì €ì¥ëœê±¸ ê³¼ê±°->ìµœì‹  ìˆœìœ¼ë¡œ ë’¤ì§‘ì–´ì„œ ë³´ì—¬ì¤Œ
  const historyList = projects[activeProject] || [];
  const displayHistory = [...historyList].reverse(); 

  // ë³´ê´€í•¨ ë¦¬ìŠ¤íŠ¸ (ì˜¤ë¥¸ìª½ì— ë³´ì—¬ì§ˆ ë‚´ìš©) - isBookmarkedê°€ trueì¸ ê²ƒë§Œ í•„í„°ë§
  const bookmarkedList = historyList.filter(item => item.isBookmarked);

  return (
    <div className="app-container">

      {/* ì™¼ìª½ ì‚¬ì´ë“œë°” */}
      <div className="sidebar">
        <div className="sidebar-top">
            <div className="logo-area">
                <h1><span style={{fontSize: '1.5rem'}}>ğŸ¨</span> ë””ìì¸ ì‹œìŠ¤í…œ ë´‡</h1>
            </div>

            {/* ğŸ”¥ ìƒˆ ì±„íŒ… ë²„íŠ¼: í™”ë©´ì„ ë¹„ìš°ê³  ìƒˆë¡œ ì‹œì‘ */}
            <button onClick={startNewChat} className="new-chat-btn">
                <MessageSquarePlus size={18} /> ìƒˆ ì±„íŒ… (í™”ë©´ ë¹„ìš°ê¸°)
            </button>

            <div className="project-list-area">
                <h2>í”„ë¡œì íŠ¸ (ëŒ€í™”ë°©)</h2>
                <div className="add-project-row">
                    <input 
                        placeholder="ìƒˆ í”„ë¡œì íŠ¸ ì´ë¦„" 
                        value={newProjectName}
                        onChange={(e) => setNewProjectName(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && addProject()}
                    />
                    <button onClick={addProject}><Plus size={18}/></button>
                </div>

                <div className="project-items">
                {Object.keys(projects).map(p => (
                    <button
                    key={p}
                    onClick={() => switchProject(p)}
                    className={`project-item ${activeProject === p ? 'active' : ''}`}
                    >
                    <Folder size={16} /> {p}
                    </button>
                ))}
                </div>
            </div>
        </div>

        <div className="user-profile">
            <div className="user-info">
                {user.picture ? <img src={user.picture} alt="profile" /> : <div className="user-avatar-placeholder"><User size={16}/></div>}
                <div className="user-text">
                    <p className="user-name">{user.name}</p>
                    <p className="user-email">{user.email}</p>
                </div>
            </div>
            <button onClick={handleLogout} className="logout-btn">ë¡œê·¸ì•„ì›ƒ</button>
        </div>
      </div>

      {/* ì¤‘ì•™ ë©”ì¸ ì˜ì—­ */}
      <div className="main-content">
        <main className="chat-area">
            {displayHistory.length === 0 && !currentData && (
                <div className="welcome-screen">
                    <h2>ì•ˆë…•í•˜ì„¸ìš” {user.name}ë‹˜,<br/>
                    <span className="highlight-text">'{activeProject}' ëŒ€í™”ë°©ì…ë‹ˆë‹¤.</span>
                    </h2>
                </div>
            )}

            {/* ğŸ“œ ëŒ€í™” íˆìŠ¤í† ë¦¬ (ì—¬ê¸°ì„œ ë¶ë§ˆí¬ í† ê¸€ ê°€ëŠ¥) */}
            {displayHistory.map((item, idx) => {
                // displayHistoryëŠ” ë’¤ì§‘í˜€ìˆìœ¼ë¯€ë¡œ ì›ë³¸ ì¸ë±ìŠ¤ë¥¼ ì°¾ì•„ì•¼ í•¨
                const originalIndex = historyList.length - 1 - idx;

                return (
                <div key={item.id || idx} className="history-item-group">
                    <div className="user-message">
                        <div className="bubble">{item.userInput || item.name}</div>
                    </div>
                    <div className="bot-response">
                        <div className="bot-avatar">ğŸ¤–</div>
                        <div className="response-card" style={{borderColor: item.isBookmarked ? '#3b82f6' : '#374151'}}>
                             <div className="card-header">
                                <div className="tag-row">
                                    {item.isBookmarked ? 
                                        <span className="ai-tag bg-blue-900 text-blue-200 flex items-center gap-1"><Star size={12} fill="currentColor"/> ë³´ê´€ë¨</span> :
                                        <span className="ai-tag bg-gray-700 text-gray-400">ê¸°ë¡ë¨</span>
                                    }
                                    <h3 className="text-xl font-bold text-white ml-2">{item.name}</h3>
                                </div>
                                <div className="flex gap-2">
                                    {/* ë¶ë§ˆí¬ í† ê¸€ ë²„íŠ¼ */}
                                    <button onClick={() => toggleBookmark(originalIndex)} className="text-gray-400 hover:text-yellow-400 transition-colors" title={item.isBookmarked ? "ë³´ê´€í•¨ì—ì„œ ì œê±°" : "ë³´ê´€í•¨ì— ì¶”ê°€"}>
                                        {item.isBookmarked ? <Star size={18} fill="#facc15" className="text-yellow-400"/> : <Star size={18} />}
                                    </button>
                                    {/* ì˜êµ¬ ì‚­ì œ ë²„íŠ¼ */}
                                    <button onClick={() => deletePermanently(originalIndex)} className="text-gray-400 hover:text-red-500 transition-colors" title="ì˜êµ¬ ì‚­ì œ">
                                        <Trash2 size={18}/>
                                    </button>
                                </div>
                            </div>
                            <div className="palette-grid">
                                {item.palette.map((color, i) => (
                                <div key={i} className="color-item">
                                    <div className={`color-box ${color.isTarget ? 'is-target' : ''}`} style={{ backgroundColor: color.hex }}></div>
                                    <p className="level-text">{color.level}</p>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
