import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
// ì•„ì´ì½˜ ì¶”ê°€ ì„í¬íŠ¸ (User, Send, Folder ë“±)
import { Trash2, Download, Plus, Save, User, Send, Folder, MessageSquarePlus } from 'lucide-react'; 

// ğŸš¨ CLIENT_IDëŠ” í˜„ì£¼ë‹˜ì˜ ì§„ì§œ IDë¡œ ìœ ì§€í•˜ì„¸ìš”!
const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  // --- ê¸°ì¡´ ë¡œì§ ìƒíƒœê°’ ìœ ì§€ ---
  const [user, setUser] = useState(null);
  const [projects, setProjects] = useState({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
  const [activeProject, setActiveProject] = useState("ê¸°ë³¸ í”„ë¡œì íŠ¸");
  const [inputHex, setInputHex] = useState("");
  const [currentData, setCurrentData] = useState(null); // í˜„ì¬ ìƒì„±ëœ ê²°ê³¼ë¬¼
  const [loading, setLoading] = useState(false);
  const [newProjectName, setNewProjectName] = useState("");
  const scrollRef = useRef(null); // ìŠ¤í¬ë¡¤ ìë™ ì´ë™ìš©

  // --- ê¸°ì¡´ ë¡œì§ í•¨ìˆ˜ ìœ ì§€ ---
  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/projects/${email}`);
      setProjects(res.data || { "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setProjects({ "ê¸°ë³¸ í”„ë¡œì íŠ¸": [] });
    setCurrentData(null);
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;
    // ì…ë ¥ê°’ ì •ê·œì‹ ì²´í¬ (ê°„ë‹¨ ë²„ì „)
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("ì˜¬ë°”ë¥¸ HEX ì½”ë“œë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš” (ì˜ˆ: #3A86FF)");
        return;
    }

    setLoading(true);
    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);

    // UIì— ë³´ì—¬ì£¼ê¸° ìœ„í•´ ìœ ì € ì…ë ¥ê°’ë„ ê°™ì´ ì €ì¥
    const userInput = formattedHex;

    try {
      const res = await axios.post('http://localhost:5000/api/ai-naming', { hex: formattedHex });
      setCurrentData({ userInput, name: res.data.name, palette, target: targetLevel });
    } catch (err) {
      setCurrentData({ userInput, name: `Color-${formattedHex}`, palette, target: targetLevel });
    }
    setLoading(false);
    setInputHex(""); // ì…ë ¥ì°½ ì´ˆê¸°í™”
  };

  // ê²°ê³¼ê°€ ë‚˜ì˜¤ë©´ ìŠ¤í¬ë¡¤ ì•„ë˜ë¡œ
  useEffect(() => {
    if (scrollRef.current) {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [currentData]);


  const saveToVault = async () => {
    if (!user || !currentData) return;
    const updatedProjects = { ...projects };
    // ì €ì¥í•  ë•ŒëŠ” userInputì€ ì œì™¸í•˜ê³  ì €ì¥
    const dataToSave = { name: currentData.name, palette: currentData.palette, target: currentData.target };
    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];

    setProjects(updatedProjects);
    await axios.post('http://localhost:5000/api/projects', {
      email: user.email,
      projects: updatedProjects
    });
  };

  const addProject = () => {
    if (!newProjectName) return;
    if (projects[newProjectName]) {
        alert("ì´ë¯¸ ì¡´ì¬í•˜ëŠ” í”„ë¡œì íŠ¸ ì´ë¦„ì…ë‹ˆë‹¤.");
        return;
    }
    setProjects({ ...projects, [newProjectName]: [] });
    setActiveProject(newProjectName);
    setNewProjectName("");
  };


  // --- ğŸ–¥ï¸ ë Œë”ë§ (ë””ìì¸ ì ìš© ë¶€ë¶„) ---

  // 1. ë¡œê·¸ì¸ í™”ë©´ (ë‹¤í¬ ëª¨ë“œ ì ìš©)
  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        {/* ë°°ê²½ìƒ‰ì„ ì–´ë‘¡ê²Œ ë³€ê²½ (bg-gray-900) */}
        <div className="flex h-screen items-center justify-center bg-gray-900 text-white">
          <div className="bg-gray-800 p-10 rounded-2xl shadow-2xl text-center border border-gray-700">
            <h1 className="text-3xl font-bold mb-8">ğŸ¨ ë””ìì¸ ì‹œìŠ¤í…œ ë´‡</h1>
            <div className="flex justify-center">
                <GoogleLogin 
                    onSuccess={handleLoginSuccess} 
                    onError={() => console.log('Login Failed')} 
                    theme="filled_black" // êµ¬ê¸€ ë²„íŠ¼ë„ ì–´ë‘ìš´ í…Œë§ˆë¡œ
                    shape="pill"
                />
            </div>
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  // 2. ë©”ì¸ í™”ë©´ (ë‹¤í¬ ëª¨ë“œ ë ˆì´ì•„ì›ƒ ì ìš©)
  return (
    // ì „ì²´ ì»¨í…Œì´ë„ˆ: ì–´ë‘ìš´ ë°°ê²½ (bg-[#121212] - ì´ë¯¸ì§€ì™€ ìœ ì‚¬í•œ ì•„ì£¼ ì–´ë‘ìš´ íšŒìƒ‰)
    <div className="flex h-screen bg-[#121212] text-gray-200 font-sans">

      {/* --- [ì™¼ìª½ ì‚¬ì´ë“œë°”] ë„¤ë¹„ê²Œì´ì…˜ --- */}
      <div className="w-64 bg-[#1E1E1E] border-r border-gray-800 p-4 flex flex-col justify-between">
        <div>
            {/* ë¡œê³ /íƒ€ì´í‹€ */}
            <div className="mb-8 px-2">
                <h1 className="text-lg font-bold text-white flex items-center gap-2">
                    <span className="text-2xl">ğŸ¨</span> ë””ìì¸ ì‹œìŠ¤í…œ ë´‡
                </h1>
            </div>

            {/* ìƒë‹¨ ë©”ë‰´ */}
            <div className="space-y-2">
                <button 
                    onClick={() => {setCurrentData(null); setInputHex("");}}
                    className="w-full flex items-center gap-3 px-4 py-3 text-sm font-medium text-gray-300 bg-gray-800 hover:bg-gray-700 rounded-xl transition-colors"
                >
                    <MessageSquarePlus size={18} /> ìƒˆ ì±„íŒ…
                </button>
            </div>

            {/* í”„ë¡œì íŠ¸ ëª©ë¡ */}
            <div className="mt-8">
                <h2 className="px-4 text-xs font-semibold text-gray-500 uppercase tracking-wider mb-2">í”„ë¡œì íŠ¸</h2>

                {/* í”„ë¡œì íŠ¸ ì¶”ê°€ ì…ë ¥ì°½ */}
                <div className="flex gap-1 mb-4 px-2">
                    <input 
                        className="bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 w-full text-sm focus:outline-none focus:border-blue-500" 
                        placeholder="ìƒˆ í”„ë¡œì íŠ¸ ì´ë¦„" 
                        value={newProjectName}
                        onChange={(e) => setNewProjectName(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && addProject()}
                    />
                    <button onClick={addProject} className="bg-gray-800 hover:bg-gray-700 border border-gray-700 text-gray-300 p-2 rounded-lg transition-colors">
                        <Plus size={18}/>
                    </button>
                </div>

                {/* í”„ë¡œì íŠ¸ ë¦¬ìŠ¤íŠ¸ */}
                <div className="space-y-1 max-h-64 overflow-y-auto px-2 custom-scrollbar">
                {Object.keys(projects).map(p => (
                    <button
                    key={p}
                    onClick={() => setActiveProject(p)}
                    className={`w-full flex items-center gap-3 px-3 py-2 text-sm rounded-lg transition-colors ${activeProject === p ? 'bg-blue-600 text-white font-bold shadow-md' : 'text-gray-400 hover:bg-gray-800 hover:text-white'}`}
                    >
                    <Folder size={16} /> {p}
                    </button>
                ))}
                </div>
            </div>
        </div>

        {/* í•˜ë‹¨ ìœ ì € ì •ë³´ */}
        <div className="border-t border-gray-800 pt-4 px-2 flex items-center justify-between">
            <div className="flex items-center gap-2">
                {user.picture ? (
                    <img src={user.picture} alt="profile" className="w-8 h-8 rounded-full border border-gray-700" />
                ) : (
                    <div className="w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center"><User size={16}/></div>
                )}
                <div className="text-sm overflow-hidden">
                    <p className="font-medium text-white truncate">{user.name}</p>
                    <p className="text-xs text-gray-500 truncate">{user.email}</p>
                </div>
            </div>
            <button onClick={handleLogout} className="text-gray-500 hover:text-red-400 p-2 transition-colors text-xs">
                ë¡œê·¸ì•„ì›ƒ
            </button>
        </div>
      </div>


      {/* --- [ì¤‘ì•™ ë©”ì¸ ì˜ì—­] ì½˜í…ì¸  + ì…ë ¥ì°½ --- */}
      <div className="flex-1 flex flex-col overflow-hidden relative">

        {/* ìƒë‹¨ í—¤ë” (ì˜¤ë¥¸ìª½ ìœ ì € ì•„ì´ì½˜ ì˜ì—­ - ë””ìì¸ìƒ ë¹„ì›Œë‘ ) */}
        <header className="h-16 flex items-center justify-end px-6 shrink-0">
            {/* í•„ìš”ì‹œ ì¶”ê°€ ë²„íŠ¼ ë°°ì¹˜ */}
        </header>

        {/* ë©”ì¸ ì½˜í…ì¸  ìŠ¤í¬ë¡¤ ì˜ì—­ */}
        <main className="flex-1 overflow-y-auto p-6 flex flex-col">

            {/* --- ìƒíƒœ 1: ì´ˆê¸° í™”ë©´ (ê²°ê³¼ê°€ ì—†ì„ ë•Œ - ì´ë¯¸ì§€ 1 ì°¸ì¡°) --- */}
            {!currentData && (
                <div className="flex-1 flex flex-col items-center justify-center pb-20">
                    <h2 className="text-3xl md:text-4xl font-bold text-white mb-6 text-center animate-fade-in">
                        ì•ˆë…•í•˜ì„¸ìš” {user.name}ë‹˜,<br/>
                        <span className="text-blue-500">ì˜¤ëŠ˜ì€ ì–´ë–¤ ì‹œìŠ¤í…œì´ í•„ìš”í•˜ì„¸ìš”?</span>
                    </h2>
                </div>
            )}

            {/* --- ìƒíƒœ 2: ê²°ê³¼ í™”ë©´ (ê²°ê³¼ê°€ ìˆì„ ë•Œ - ì´ë¯¸ì§€ 2 ì°¸ì¡°) --- */}
            {currentData && (
                <div className="max-w-3xl mx-auto w-full space-y-8 pb-10">
                    {/* ìœ ì € ì§ˆë¬¸ (ì˜¤ë¥¸ìª½ ë§í’ì„ ) */}
                    <div className="flex justify-end animate-fade-in-up">
                        <div className="bg-blue-600 text-white px-6 py-3 rounded-2xl rounded-tr-sm text-lg shadow-md max-w-md break-words">
                            {currentData.userInput} ë§Œë“¤ì–´ì¤˜
                        </div>
                    </div>

                    {/* ë´‡ ì‘ë‹µ (ì™¼ìª½ ì½˜í…ì¸  ì˜ì—­) */}
                    <div className="flex gap-4 animate-fade-in-up delay-100" ref={scrollRef}>
                        <div className="w-10 h-10 rounded-full bg-gradient-to-tr from-purple-500 to-blue-500 flex shrink-0 items-center justify-center shadow-lg">
                            <span className="text-xl">ğŸ¤–</span>
                        </div>
