import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleOAuthProvider, GoogleLogin, googleLogout } from '@react-oauth/google';
import { jwtDecode } from "jwt-decode";
import { calculatePalette } from './utils';
import { Trash2, Plus, Save, User, Send, Folder, MessageSquarePlus } from 'lucide-react'; 
import './App.css'; 

// üö® CLIENT_ID Ïú†ÏßÄ
const CLIENT_ID = "997761035180-ho629l7o1e8ec1qhkmp6ona5mll5nbb5.apps.googleusercontent.com"; 

function App() {
  // 1. [ÏàòÏ†ï] ÏÉàÎ°úÍ≥†Ïπ® Ìï¥ÎèÑ Î°úÍ∑∏Ïù∏ Ïú†ÏßÄÎ•º ÏúÑÌï¥ localStorageÏóêÏÑú ÏùΩÏñ¥Ïò§Í∏∞
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('designBotUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [projects, setProjects] = useState({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
  const [activeProject, setActiveProject] = useState("Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏");
  const [inputHex, setInputHex] = useState("");

  // 2. [ÏàòÏ†ï] ÏûëÏóÖ Ï§ëÏù¥Îçò Ïª¨Îü¨ÎèÑ localStorageÏóêÏÑú ÏùΩÏñ¥Ïò§Í∏∞ (ÏÉàÎ°úÍ≥†Ïπ® ÎåÄÎπÑ)
  const [currentData, setCurrentData] = useState(() => {
    const savedWork = localStorage.getItem('currentWork');
    return savedWork ? JSON.parse(savedWork) : null;
  });

  const [loading, setLoading] = useState(false);
  const [newProjectName, setNewProjectName] = useState("");
  const scrollRef = useRef(null);

  // Î°úÍ∑∏Ïù∏ & Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    if (user && user.email) {
      fetchUserData(user.email);
    }
  }, [user]);

  // ÏûëÏóÖ ÎÇ¥Ïö©Ïù¥ Î∞îÎÄî ÎïåÎßàÎã§ localStorageÏóê Ï†ÄÏû•
  useEffect(() => {
    localStorage.setItem('currentWork', JSON.stringify(currentData));
  }, [currentData]);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    localStorage.setItem('designBotUser', JSON.stringify(decoded)); // Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ Ï†ÄÏû•
    fetchUserData(decoded.email);
  };

  const fetchUserData = async (email) => {
    try {
      const res = await axios.get(`http://localhost:5001/api/projects/${email}`);
      setProjects(res.data || { "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    } catch (err) { 
      console.error(err);
      setProjects({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    }
  };

  const handleLogout = () => {
    googleLogout();
    setUser(null);
    setCurrentData(null);
    setProjects({ "Í∏∞Î≥∏ ÌîÑÎ°úÏ†ùÌä∏": [] });
    localStorage.removeItem('designBotUser'); // Ï†ÄÏû•Îêú Î°úÍ∑∏Ïù∏ ÏÇ≠Ï†ú
    localStorage.removeItem('currentWork');   // ÏûëÏóÖ ÎÇ¥Ïö© ÏÇ≠Ï†ú
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    if (!inputHex) return;
    const hexRegex = /^#?([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/;
    if (!hexRegex.test(inputHex)) {
        alert("Ïò¨Î∞îÎ•∏ HEX ÏΩîÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî (Ïòà: #3A86FF)");
        return;
    }

    setLoading(true);
    const formattedHex = inputHex.startsWith("#") ? inputHex : "#" + inputHex;
    const { palette, targetLevel } = calculatePalette(formattedHex);
    const userInput = formattedHex;

    try {
      const res = await axios.post('http://localhost:5001/api/ai-naming', { hex: formattedHex });
      setCurrentData({ userInput, name: res.data.name, palette, target: targetLevel });
    } catch (err) {
      setCurrentData({ userInput, name: `Color-${formattedHex}`, palette, target: targetLevel });
    }
    setLoading(false);
    setInputHex("");
  };

  useEffect(() => {
    if (scrollRef.current) {
        scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [currentData]);

  const saveToVault = async () => {
    if (!user || !currentData) return;
    const updatedProjects = { ...projects };
    // Ï†ÄÏû•Ìï† Îïå userInputÎèÑ Í∞ôÏù¥ Ï†ÄÏû•Ìï¥Ïïº ÎÇòÏ§ëÏóê Î∂àÎü¨Ïò¨ Îïå ÎßêÌíçÏÑ†Ïù¥ ÎúπÎãàÎã§.
    const dataToSave = { 
        userInput: currentData.userInput || currentData.name, // ÏóÜÏúºÎ©¥ Ïù¥Î¶ÑÏúºÎ°ú ÎåÄÏ≤¥
        name: currentData.name, 
        palette: currentData.palette, 
        target: currentData.target 
    };
    updatedProjects[activeProject] = [dataToSave, ...(updatedProjects[activeProject] || [])];

    setProjects(updatedProjects);
    await axios.post('http://localhost:5001/api/projects', {
      email: user.email,
      projects: updatedProjects
    });
  };

  const addProject = () => {
    if (!newProjectName) return;
    if (projects[newProjectName]) {
        alert("Ïù¥ÎØ∏ Ï°¥Ïû¨ÌïòÎäî ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏûÖÎãàÎã§.");
        return;
    }
    setProjects({ ...projects, [newProjectName]: [] });
    setActiveProject(newProjectName);
    setNewProjectName("");
  };

  // 3. [Ï∂îÍ∞Ä] Î≥¥Í¥ÄÌï® ÏïÑÏù¥ÌÖú ÌÅ¥Î¶≠ Ïãú Î©îÏù∏ ÌôîÎ©¥ÏúºÎ°ú Î∂àÎü¨Ïò§Í∏∞ Ìï®Ïàò
  const loadFromVault = (item) => {
    setCurrentData(item);
    if(scrollRef.current) scrollRef.current.scrollIntoView({ behavior: 'smooth' });
  };


  // --- Î†åÎçîÎßÅ ---

  if (!user) {
    return (
      <GoogleOAuthProvider clientId={CLIENT_ID}>
        <div className="login-container">
          <div className="login-box">
            <h1>üé® ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú Î¥á</h1>
            <div className="login-btn-wrapper">
                <GoogleLogin 
                    onSuccess={handleLoginSuccess} 
                    onError={() => console.log('Login Failed')} 
                    theme="filled_black" 
                    shape="pill"
                />
            </div>
          </div>
        </div>
      </GoogleOAuthProvider>
    );
  }

  return (
    <div className="app-container">

      {/* ÏôºÏ™Ω ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <div className="sidebar">
        <div className="sidebar-top">
            <div className="logo-area">
                <h1><span style={{fontSize: '1.5rem'}}>üé®</span> ÎîîÏûêÏù∏ ÏãúÏä§ÌÖú Î¥á</h1>
            </div>

            <button 
                onClick={() => {setCurrentData(null); setInputHex("");}}
                className="new-chat-btn"
            >
                <MessageSquarePlus size={18} /> ÏÉà Ï±ÑÌåÖ
            </button>

            <div className="project-list-area">
                <h2>ÌîÑÎ°úÏ†ùÌä∏</h2>
                <div className="add-project-row">
                    <input 
                        placeholder="ÏÉà ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ" 
                        value={newProjectName}
                        onChange={(e) => setNewProjectName(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && addProject()}
                    />
                    <button onClick={addProject}><Plus size={18}/></button>
                </div>

                <div className="project-items">
                {Object.keys(projects).map(p => (
                    <button
                    key={p}
                    onClick={() => setActiveProject(p)}
                    className={`project-item ${activeProject === p ? 'active' : ''}`}
                    >
                    <Folder size={16} /> {p}
                    </button>
                ))}
                </div>
            </div>
        </div>

        <div className="user-profile">
            <div className="user-info">
                {user.picture ? (
                    <img src={user.picture} alt="profile" />
                ) : (
                    <div className="user-avatar-placeholder"><User size={16}/></div>
                )}
                <div className="user-text">
                    <p className="user-name">{user.name}</p>
                    <p className="user-email">{user.email}</p>
                </div>
            </div>
            <button onClick={handleLogout} className="logout-btn">Î°úÍ∑∏ÏïÑÏõÉ</button>
        </div>
      </div>

      {/* Ï§ëÏïô Î©îÏù∏ ÏòÅÏó≠ */}
      <div className="main-content">
        <main className="chat-area">
            {!currentData && (
                <div className="welcome-screen">
                    <h2>ÏïàÎÖïÌïòÏÑ∏Ïöî {user.name}Îãò,<br/>
                    <span className="highlight-text">Ïò§ÎäòÏùÄ Ïñ¥Îñ§ ÏãúÏä§ÌÖúÏù¥ ÌïÑÏöîÌïòÏÑ∏Ïöî?</span>
                    </h2>
                </div>
            )}

            {currentData && (
                <div className="result-screen">
                    <div className="user-message">
                        <div className="bubble">
                            {currentData.userInput || currentData.name} ÎßåÎì§Ïñ¥Ï§ò
                        </div>
                    </div>

                    <div className="bot-response" ref={scrollRef}>
                        <div className="bot-avatar">ü§ñ</div>
                        <div className="response-card">
                            <div className="card-header">
                                <div>
                                    <div className="tag-row">
                                        <span className="ai-tag">AI Ï∂îÏ≤ú ÎÑ§Ïù¥Î∞ç</span>
                                        <span className="target-tag">‚Ä¢ Target {currentData.target}</span>
                                    </div>
                                    <input 
                                        className="naming-input"
                                        value={currentData.name}
                                        onChange={(e) => setCurrentData({...currentData, name: e.target.value})}
                                    />
                                </div>
                                <button onClick={saveToVault} className="save-btn">
                                    <Save size={16} /> Î≥¥Í¥ÄÌï® Ï†ÄÏû•
                                </button>
                            </div>

                            <div className="palette-grid">
                                {currentData.palette.map((color, idx) => (
                                <div key={idx} className="color-item">
                                    <div 
                                    className={`color-box ${color.isTarget ? 'is-target' : ''}`}
                                    style={{ backgroundColor: color.hex }}
                                    title={color.hex}
                                    >
                                    {color.isTarget && <span className="target-dot"></span>}
                                    </div>
                                    <p className={`level-text ${color.isTarget ? 'target-text' : ''}`}>{color.level}</p>
                                </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </main>

        <div className="input-area">
            <form onSubmit={handleGenerate} className="input-form">
                <input 
                    placeholder="HEX ÏΩîÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (Ïòà: #3A86FF)"
                    value={inputHex}
                    onChange={(e) => setInputHex(e.target.value)}
                    disabled={loading}
                />
                <button type="submit" disabled={loading || !inputHex} className={loading || !inputHex ? 'disabled' : ''}>
                    {loading ? <div className="spinner"/> : <Send size={20} />}
                </button>
            </form>
        </div>
      </div>

      {/* Ïò§Î•∏Ï™Ω ÏÇ¨Ïù¥ÎìúÎ∞î (Î≥¥Í¥ÄÌï®) */}
      {currentData && (
        <div className="vault-sidebar">
          <h3>
            <Folder size={20} style={{color: '#3b82f6'}}/> ÎÇ¥ Î≥¥Í¥ÄÌï®
            <span className="vault-project-name">ÌòÑÏû¨: {activeProject}</span>
          </h3>

          <div className="vault-list">
            {projects[activeProject]?.length === 0 ? (
                <p className="empty-msg">Ï†ÄÏû•Îêú ÏïÑÏù¥ÌÖúÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            ) : (
                projects[activeProject]?.slice().reverse().map((item, idx) => (
                // 4. [ÏàòÏ†ï] ÌÅ¥Î¶≠ÌïòÎ©¥ loadFromVault Ïã§Ìñâ (Î©îÏù∏ ÌôîÎ©¥ÏúºÎ°ú Î∂àÎü¨Ïò§Í∏∞)
                <div 
                    key={idx} 
                    className="vault-item" 
                    onClick={() => loadFromVault(item)} // üëà Ïó¨Í∏∞Í∞Ä ÌïµÏã¨! ÌÅ¥Î¶≠ÌïòÎ©¥ Î∂àÎü¨Ïò¥
                    style={{cursor: 'pointer'}}
                >
                    <div className="vault-header">
                        <h4>{item.name}</h4>
                        <button onClick={async (e) => {
                            e.stopPropagation(); // Î≤ÑÌäº ÎàåÎ†ÄÏùÑ Îïê Î∂àÎü¨Ïò§Í∏∞ Î∞©ÏßÄ
                            const newProjects = {...projects};
                            const realIndex = newProjects[activeProject].length - 1 - idx;
                            newProjects[activeProject].splice(realIndex, 1);
                            setProjects(newProjects);
                            await axios.post('http://localhost:5001/api/projects', {
                                email: user.email,
                                projects: newProjects
                            });
                        }} className="delete-btn"><Trash2 size={14}/></button>
                    </div>
                    <div className="mini-palette">
                        {item.palette.map((p, i) => (
                        <div key={i} style={{backgroundColor: p.hex, flex: 1}}></div>
                        ))}
                    </div>
                    <div className="vault-footer">
                        <span>Target: {item.target}</span>
                        <span>{item.palette.length} colors</span>
                    </div>
                </div>
                ))
            )}
          </div>
        </div>
      )}

    </div>
  );
}

export default App;
